// Test service callback api via grpc client
func Test_service_callback_methods(t *testing.T) {
	conn := getRPCClientConnForTest()
	cli := stockV1.NewCallbackClient(conn)
	ctx, _ := context.WithTimeout(context.Background(), time.Second*30)

	tests := []struct {
		name    string
		fn      func() (interface{}, error)
		wantErr bool
	}{

		{
			name: "QueryPrepared",
			fn: func() (interface{}, error) {
				// todo type in the parameters before testing
				req := &stockV1.QueryPreparedRequest{}

				return cli.QueryPrepared(ctx, req)
			},
			wantErr: false,
		},

		{
			name: "DeleteCache",
			fn: func() (interface{}, error) {
				// todo type in the parameters before testing
				req := &stockV1.DeleteCacheRequest{
					Key: "",
				}

				return cli.DeleteCache(ctx, req)
			},
			wantErr: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := tt.fn()
			if (err != nil) != tt.wantErr {
				t.Errorf("test '%s' error = %v, wantErr %v", tt.name, err, tt.wantErr)
				return
			}
			data, _ := json.MarshalIndent(got, "", "    ")
			fmt.Println(string(data))
		})
	}
}

